import React from 'react';
import './Main.css';   
class Main extends React.Component {    
    componentDidMount(){
        this.buttonid.addEventListener('click, this.openDialog')
    }

    componentWillUnmount
  constructor(props) {
    super(props);

    this.state = {
      imageURL: '',
    };

    this.handleUploadImage = this.handleUploadImage.bind(this);      
  }    

  handleUploadImage(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('file', this.uploadInput.files[0]);
    data.append('filename', this.fileName.value);

    fetch('http://localhost:8000/upload', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ imageURL: `http://localhost:8000/${body.file}` });
      });
    });
  }



  render() {
    return (
      <div>
        <header className="App-header">
        <h1>Image Processor</h1>
      </header>
      <div className="wrapper">
        <div id="one">
          <h1>Original image</h1>
          <div className='container'>
            <img id="originalImg" className='img' src="https://www.w3schools.com/howto/img_snow_wide.jpg" alt="Snow" />
          </div>
        </div>
        <div id="two">
          <h1>Processed image</h1>
          <div className='container'>
            <img id="processImg" className='img' src="https://www.w3schools.com/howto/img_snow_wide.jpg" alt="Snow" />
          </div>
        </div>
        <div className='buttonsContainer'>
          <input ref={fileid} id='fileid' type='file' hidden />
          <input ref={elem => this.buttonid = elem} id='buttonid' type="button" className='button' value="Upload the image" />
          <input id='process' type="button" className='button' value="Process the image" />
        </div>
      </div>
      </div>
    );
  }
}

export default Main;
