import React from 'react';
import './Main.css';
class Main extends React.Component {

  componentDidMount() {
    this.uploadButton.addEventListener('click', this.openDialog)
  }

  componentWillUnmount() {
    this.uploadButton.removeEventListener('click', this.openDialog)
  }

  openDialog = (event) => {
    this.uploadInput.click();
  }

  constructor(props) {
    super(props);

    this.state = {
      imageURL: '',
    };

    this.handleUploadImage = this.handleUploadImage.bind(this);
  }

  handleUploadImage(ev) {
    ev.preventDefault();
    const data = new FormData();
    data.append('file', this.uploadInput.files[0]);
    /*data.append('filename', this.fileName.value);*/


    fetch('http://localhost:5000/upload', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ imageURL: `http://localhost:5000/${body.file}` });
        console.log(body.file);
      });
    });    
    this.orginalImg.className = "img";
  }



  render() {
    return (
      <div>
        <header className="App-header">
          <h1>Image Processor</h1>
        </header>
        <div className="wrapper">
          <div id="one">
            <h1>Original image</h1>
            <div className='container'>
              <img ref={elem => this.orginalImg = elem} id="originalImg" className='originalImg' src={this.state.imageURL} alt="Snow" />
            </div>
          </div>
          <div id="two">
            <h1>Processed image</h1>
            <div className='container'>
              <img id="processImg" className='processedImg' src="https://www.w3schools.com/howto/img_snow_wide.jpg" alt="Snow" />
            </div>
          </div>
          <div className='buttonsContainer'>
            <input ref={elem => this.uploadInput = elem} onChange={this.handleUploadImage} id='uploadInput' type='file' accept="image/jpeg, image/png" hidden />
            <input ref={elem => this.uploadButton = elem} id='uploadButton' type="button" className='button' value="Upload the image" />
            <input ref={elem => this.processButton = elem}  id='process' type="button" className='button' value="Process the image" />
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
